jQuery(document).ready(function(){image_overlay=jQuery('a.thumbnail').overlay({target:'#overlay',onClose:function(){jQuery("#overlayPadder").html('');},expose:{color:'#000000',loadSpeed:300,opacity:0.5}});jQuery('a.simple_popup').click(function(){jQuery(this).populateOverlayScreen(jQuery(this));});jQuery("a[id='marvin']").click(function(){jQuery("#overlayPadder").load('/our_work/video_marvin');});jQuery("a[id='showreel']").click(function(){jQuery("#overlayPadder").load('/our_work/video_showreel');});jQuery("a[id='plum']").click(function(){jQuery("#overlayPadder").load('/our_work/video_plum');});jQuery("a[id='sat_morn']").click(function(){jQuery("#overlayPadder").load('/our_work/video_sat_morn');});jQuery('a#close').click(function(){jQuery('a.thumbnail').each(function(){jQuery(this).overlay().close();});});});jQuery.fn.populateOverlayScreen=function(element){jQuery(".overlayPadder").replaceWith("<div class=\"overlayPadder\" id=\"overlayPadder\"><h2>"+element[0].title+"</h2><img src=\""+element[0].href+"\" /></div>");};